import React, {Component} from 'react'

class Secrets extends Component {

	constructor() {
		super();
		this.state = {
			secrets: [{"id":1,"content":"Test ","created_at":"2019-05-07T01:32:47.342Z","updated_at":"2019-05-07T01:32:47.342Z"}]
		}
		this.saveSecret = this.saveSecret.bind(this);
	}

	saveSecret(content) {
		// turn th content into a secret object
		const secret = {
		id: Math.random(),
		content: content
	};
	const newSecrets = this.state.secrets.slice(0);
	newSecrets.push( secret );

	this.setState({secrets: newSecrets})
		//add the secret to the colllection of secrets in the state
	}

	render () {
		return (
			<div>
			<h1>Tell us all your secrets</h1>
			<SecretForm onSubmit={this.saveSecret} />
			<Gallery secrets={ this.state.secrets } />
			</div>
		);
	}
};

class SecretForm extends Component {
	constructor () {
		super();
		this.state = { content: '' };
		this._handleSubmit = this._handleSubmit.bind(this);
		this._handleChange = this._handleChange.bind(this);
	}

	_handleSubmit(e) {
		e.preventDefault();
		this.props.onSubmit
	}

	_handleChange(e) {
		this.setState({ content: e.target.value });
	}

	render() {
		return (
			<form onSubmit={this._handleSubmit}>
				<textarea onChange={this._handleChange}></textarea>
				<input type="submit" value="Tell" />
			</form>
		)
	}
}

class Gallery extends Component {
	render() {
		return (
			<form>
			<div>
				{this.props.secrets.map( (s) => <p key={ s.id }>{s.content}</p> ) }
			</div>
			</form>
		)
	}
}

export default Secrets;
