import React, {Component} from 'react'
import axios from 'axios'

const SERVER_URL = 'http://localhost:3000/secrets.json' // change this to a heroku url once deployed

class Secrets extends Component {

	constructor() {
		super();
		this.state = {
			secrets: []
		}
		this.saveSecret = this.saveSecret.bind(this);

		axios.get(SERVER_URL).then( (results) => {
			console.log(results.data);
			this.setState({ secrets: results.data })
		})
		//make those secrets ther new setState
	}

	saveSecret(content) {
	// 	// turn th content into a secret object
	// 	const secret = {
	// 	id: Math.random(),
	// 	content: content
	// };
	//
	// const newSecrets = this.state.secrets.slice(0);
	// newSecrets.push( secret );

	axios.post(SERVER_URL, { content: content }).then ((result) => {
		console.log(result)
		const newSecrets = this.state.secrets.slice(0);
		newSecrets.push( results.data )
			this.setState({secrets: newSecrets})
	});



		//add the secret to the colllection of secrets in the state
	}

	render () {
		return (
			<div>
			<h1>Tell us all your secrets</h1>
			<SecretForm onSubmit={this.saveSecret} />
			<Gallery secrets={ this.state.secrets } />
			</div>
		);
	}
};

class SecretForm extends Component {
	constructor () {
		super();
		this.state = { content: '' };
		this._handleSubmit = this._handleSubmit.bind(this);
		this._handleChange = this._handleChange.bind(this);
	}

	_handleSubmit(e) {
		e.preventDefault();
		this.props.onSubmit( this.state.content );
		this.setState({content: ''});
	}

	_handleChange(e) {
		this.setState({ content: e.target.value });
	}

	render() {
		return (
			<form onSubmit={this._handleSubmit}>
				<textarea onChange={this._handleChange} value={this.state.content }></textarea>
				<input type="submit" value="Tell" />
			</form>
		)
	}
}

class Gallery extends Component {
	render() {
		return (
			<form>
			<div>
				{this.props.secrets.map( (s) => <p key={ s.id }>{s.content}</p> ) }
			</div>
			</form>
		)
	}
}

export default Secrets;
